};
local is_searching = false;
local is_start = false;

local show_path_tips = false;
local show_map_tips = false;
local last_pick_x;
local last_pick_z;
local map_info = nil;
local last_time = 0;
function reset()
    is_searching = false;
    last_pick_x = nil;
    last_pick_z = nil;
end
function setHeroWorldPosition(x,y,z)
    hero_world_x = x;
    hero_world_y = y;
    hero_world_z = z;

    _G.block_game.hero_born_y = hero_world_y;
end
_G.block_game.setHeroWorldPosition = setHeroWorldPosition;
function getHeroWorldPosition()
    return hero_world_x,hero_world_y,hero_world_z;
end
function getRelativePositionInMap(world_x,world_z)
    local x = world_x - map_start_x + 1; 
    local z = world_z - map_start_z + 1; 
    return x,z;
end
function createKey(x,z)
    local key = string.format("%d_%d",x,z);
    return key;
end
function changeHeroState(state,path_list)
    local old_state = _G.block_game.hero_state;
    if(state == old_state)then
        return
    end
    _G.block_game.hero_state = state;

    broadcast("onHeroState",{
        path_list = path_list,
    });
end

function setMapDetail()
    local map_detail = _G.block_game.map_detail;
    local z,v;
    for z,v in ipairs(map_source) do
        local x,vv;
        for x,vv in ipairs(v) do
            local key = createKey(x,z);
            map_detail[key] = vv;
        end
    end
    local max_x = 0;
    local max_z = 0;
    if(map_source and map_source[1])then
        max_x = table.getn(map_source[1]);
        max_z = table.getn(map_source);
    end
    map_info = {
        max_x = max_x,
        max_z = max_z,
    }
    local map_info_str = string.format( "the map info:max_x=%d max_z=%d",max_x,max_z);
    log(map_info_str);
end
function changePathToWorldPosition(path_list)
    if(path_list)then
        local k,v;
        for k,v in ipairs(path_list) do
            v.x = v.x + map_start_x - 1;
            v.z = v.z + map_start_z - 1;
        end
    end
end
function autoCreateMob()
    local map_detail = _G.block_game.map_detail;
    local max_x = map_info.max_x;
    local max_z = map_info.max_z;

    local x = math.random(1,max_x);
    local z = math.random(1,max_z);
    local key = createKey(x,z);
end
function onStart()
    if(not is_start)then
        is_start = true;
    end
    playSound("worlds/DesignHouse/BlockPveGame/sounds/loading.mp3");
    showVariable("game_tip","加载游戏中,请稍等...",color_red);
    cmd("/mode game");
    log("=========start game");
    setHeroWorldPosition(hero_born_x,hero_born_y,hero_born_z);
    setMapDetail();
    log("=========start to broadcast");

    if(show_map_tips)then
        broadcast("onBuildMap",{    map_start_x = map_start_x,
                                map_start_y = map_start_y,
                                map_start_z = map_start_z,
        });
    end

    broadcast("onBorn",{    hero_world_x = hero_world_x, 
                            hero_world_y = hero_world_y, 
                            hero_world_z = hero_world_z, 
    });
    playSound("worlds/DesignHouse/BlockPveGame/sounds/start.mp3");
    showVariable("game_tip","游戏开始",color_green);

end
function canPick(w_x,w_y,w_z)
    if(is_searching)then
        return
    end
    if(not w_x or not w_y or not w_z)then
        return
    end
    if(w_x == last_pick_x and w_z == last_pick_z)then
        return
    end
    
    local map_detail = _G.block_game.map_detail;
    local x,z = getRelativePositionInMap(w_x,w_z);
    local key = createKey(x,z);
    local block_value = map_detail[key];
    if(block_value == 0)then
        return true;
    end
end
function update()
    broadcast("onRefreshMob");
end
function doPick()
    local x, y, z = mousePickBlock();
    if(not canPick(x,y,z))then
        --log("can't pick now!");
        return
    end
    showVariable("game_tip",string.format( "Pick x = %d, z = %d",x,z),color_green);

    last_pick_x = x;
    last_pick_z = z;
    is_searching = true;
    local start_x,start_z = getRelativePositionInMap(hero_world_x,hero_world_z);
    local end_x,end_z = getRelativePositionInMap(x,z);
    -- start_x = 14;
    -- start_z = 17;
    -- end_x = 7;
    -- end_z = 12;
    local msg = {
        start_x = start_x,
        start_z = start_z,
        end_x = end_x,
        end_z = end_z,
    }
    changeHeroState("wait");
    broadcast("onSearching",msg);
end
registerBroadcastEvent("onSearchFinished", function(msg)
        is_searching = false;
        changePathToWorldPosition(msg.path_list);

        changeHeroState("walking",msg.path_list);

        if(show_path_tips)then
            broadcast("onPathtips",{
                path_list = msg.path_list,
                hero_world_y = hero_world_y,
            });
        end
end)
function game_exit()
    playSound("worlds/DesignHouse/BlockPveGame/sounds/ending.mp3");
    cmd("/mode edit");
    exit();
end
while(true) do
    if(isKeyPressed("t")) then
        onStart();
    elseif(isKeyPressed("y"))then
        doPick();
    elseif(isMouseDown())then
        doPick();
    elseif(isKeyPressed("1"))then
        changeHeroState("attack");
    elseif(isKeyPressed("2"))then
        changeHeroState("wait");
    elseif(isKeyPressed("3"))then
        changeHeroState("attacked");
    elseif(isKeyPressed("u"))then
        reset();

    elseif(isKeyPressed("4"))then
        _G.block_game.mob_state = "attack";
    elseif(isKeyPressed("5"))then
        _G.block_game.mob_state = "wait";
    elseif(isKeyPressed("6"))then
        _G.block_game.mob_state = "attacked";
    elseif(isKeyPressed("0"))then
        game_exit();
    end
    
    if(is_start)then
        local now = getTimer();
        local time = now - last_time;
        --in seconds
        if(time > 5)then
            update();
            last_time = now;
        end
    end
end
